mapper.delete:
UPDATE
    device
SET
    isValid = #{isValid}
WHERE
    deviceId = #{deviceId}

mapper.select:
SELECT
    *
FROM
    device
WHERE
    1=1

    AND deviceId = #{deviceId}


    AND deviceName = #{deviceName}


    AND serial = #{serial}


    AND deviceVerifyCode = #{deviceVerifyCode}


    AND deviceType = #{deviceType}


    AND softVersion = #{softVersion}


    AND productDate = #{productDate}


    AND deviceStatus = #{deviceStatus}


    AND isOnline = #{isOnline}


    AND ipAddress = #{ipAddress}


    AND isValid = #{isValid}

SELECT
*
FROM
device
WHERE
1=1

    AND deviceId = #{deviceId}


    AND deviceName = #{deviceName}


    AND serial = #{serial}


    AND deviceVerifyCode = #{deviceVerifyCode}


    AND deviceType = #{deviceType}


    AND softVersion = #{softVersion}


    AND productDate = #{productDate}


    AND deviceStatus = #{deviceStatus}


    AND isOnline = #{isOnline}


    AND ipAddress = #{ipAddress}


    AND isValid = #{isValid}

ORDER BY
    createTime
DESC
LIMIT
    #{start}, #{pageSize}

